#include <iostream>using namespace std;void  mult_mat(double a[3][3],  double b[3][3]){ double c[3][3];double sum=0;for (int i=0;i<3;i++){for (int ii=0;ii<3;ii++){int j=0;while (j<3){sum +=a[i][j]*b[j][ii];j++;}c[i][ii]=sum;sum=0;}}for (int i=0;i<3;i++){for (int j=0;j<3;j++){cout<<c[i][j]<<"  ";}cout<<"\n";}}int main(){double  a[3][3]={{4,5,6},{1,2,3},{7,8,9} };double  b[3][3]={{1,2,3},{1,2,3},{1,2,3} };mult_mat(a,b);return 0;}